import React, { useEffect, useState } from 'react'
import {Row, Col, Button, Form, Card} from 'react-bootstrap'
import axios from 'axios'
import Book from './Book';

const BookPage = () => {
    const [list, setList] = useState([]);
    const [loading, setLoading] = useState(false);

    const getData = async() => {
        const url = "https://dapi.kakao.com/v3/search/book?target=title";
        const config = {
            headers: {"Authorization": "KakaoAK b80880fbde422de3fd9b4a4e67c9bb54"},
            params: {"query": '리액트', "size": 8, "page": 1}
        }
        setLoading(true);
        const result = await axios.get(url, config);
        setList(result.data.documents);
        setLoading(false);
    }

    useEffect(()=>{
        getData();
    },[]);

    if(loading) return <h1 className='text-center my-5'>로딩중......</h1>
    return(
        <Row>
            <h1 className = "text-center my-5">도서검색</h1>
            <Row>
                {list.map(book=>
                    <Col key={book.isbn} md={3} xs={6} className="my-2">
                        <Card>
                            <Card.Body>
                                <img src={book.thumbnail}></img>
                                <div className = "ellipsis">{book.title}</div>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
            </Row>
        </Row>
    )
}

export default BookPage
